import {useEffect} from "react";
import './App.css';
import SearchIcon from './search.svg'

//fa3668fa

const API_URL = "http://www.omdbapi.com?apikey=fa3668fa";
const App = () => {
    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`);
    const data = await response.json();
    console.log(data);
    }
    useEffect(() => {
        searchMovies("Harry Potter");
    }, []);
    return (
        <div className="app">
            <h1>MovieLand</h1>
            <div className="search">
                <input type="text" placeholder="Search for a movie..." value="Harry Potter" />
                <button>
                    <img src={SearchIcon} alt="Search" />
                </button>
            </div>
        </div>
    )
}

export default App;
